pageData.examples = {
  'Basic with right aligned legend': props => 
    <Example {...pageData} {...props} {...{"code":"import React from 'react';\nimport { Chart, ChartAxis, ChartGroup, ChartLine, ChartVoronoiContainer } from '@patternfly/react-charts';\n\nconst BasicWithRightAlignedLegend = () => (\n  <div style={{ height: '250px', width: '600px' }}>\n    <Chart\n      ariaDesc=\"Average number of pets\"\n      ariaTitle=\"Line chart example\"\n      containerComponent={<ChartVoronoiContainer labels={({ datum }) => `${datum.name}: ${datum.y}`} constrainToVisibleArea />}\n      legendData={[{ name: 'Cats' }, { name: 'Dogs', symbol: { type: 'dash' } }, { name: 'Birds' }, { name: 'Mice' }]}\n      legendOrientation=\"vertical\"\n      legendPosition=\"right\"\n      height={250}\n      maxDomain={{y: 10}}\n      minDomain={{y: 0}}\n      padding={{\n        bottom: 50,\n        left: 50,\n        right: 200, // Adjusted to accommodate legend\n        top: 50\n      }}\n      width={600}\n    >\n      <ChartAxis tickValues={[2, 3, 4]} />\n      <ChartAxis dependentAxis showGrid tickValues={[2, 5, 8]} />\n      <ChartGroup>\n        <ChartLine\n          data={[\n            { name: 'Cats', x: '2015', y: 1 },\n            { name: 'Cats', x: '2016', y: 2 },\n            { name: 'Cats', x: '2017', y: 5 },\n            { name: 'Cats', x: '2018', y: 3 }\n          ]}\n        />\n        <ChartLine\n          data={[\n            { name: 'Dogs', x: '2015', y: 2 },\n            { name: 'Dogs', x: '2016', y: 1 },\n            { name: 'Dogs', x: '2017', y: 7 },\n            { name: 'Dogs', x: '2018', y: 4 }\n          ]}\n          style={{\n            data: {\n              strokeDasharray: '3,3'\n            }\n          }}\n        />\n        <ChartLine\n          data={[\n            { name: 'Birds', x: '2015', y: 3 },\n            { name: 'Birds', x: '2016', y: 4 },\n            { name: 'Birds', x: '2017', y: 9 },\n            { name: 'Birds', x: '2018', y: 5 }\n          ]}\n        />\n        <ChartLine\n          data={[\n            { name: 'Mice', x: '2015', y: 3 },\n            { name: 'Mice', x: '2016', y: 3 },\n            { name: 'Mice', x: '2017', y: 8 },\n            { name: 'Mice', x: '2018', y: 7 }\n          ]}\n        />\n      </ChartGroup>\n    </Chart>\n  </div>\n)","title":"Basic with right aligned legend","lang":"js"}}>

    </Example>,
  'Green with bottom aligned legend': props => 
    <Example {...pageData} {...props} {...{"code":"import React from 'react';\nimport { Chart, ChartAxis, ChartGroup, ChartLine, ChartThemeColor, ChartLegendTooltip, ChartVoronoiContainer, createContainer } from '@patternfly/react-charts';\n\nclass BottomAlignedLegend extends React.Component {\n  render() {\n    // Note: Container order is important\n    const CursorVoronoiContainer = createContainer(\"voronoi\", \"cursor\");\n    const legendData = [{ childName: 'cats', name: 'Cats' }, { childName: 'dogs', name: 'Dogs', symbol: { type: 'dash' }}, { childName: 'birds', name: 'Birds' }, { childName: 'mice', name: 'Mice' }];\n\n    return (\n      <div style={{ height: '275px', width: '450px' }}>\n        <Chart\n          ariaDesc=\"Average number of pets\"\n          ariaTitle=\"Line chart example\"\n          containerComponent={\n            <CursorVoronoiContainer\n              cursorDimension=\"x\"\n              labels={({ datum }) => `${datum.y}`}\n              labelComponent={<ChartLegendTooltip legendData={legendData} title={(datum) => datum.x}/>}\n              mouseFollowTooltips\n              voronoiDimension=\"x\"\n              voronoiPadding={50}\n            />\n          }\n          legendData={legendData}\n          legendPosition=\"bottom\"\n          height={275}\n          maxDomain={{y: 10}}\n          minDomain={{y: 0}}\n          padding={{\n            bottom: 75, // Adjusted to accommodate legend\n            left: 50,\n            right: 50,\n            top: 50\n          }}\n          themeColor={ChartThemeColor.green}\n          width={450}\n        >\n          <ChartAxis tickValues={[2, 3, 4]} />\n          <ChartAxis dependentAxis showGrid tickValues={[2, 5, 8]} />\n          <ChartGroup>\n            <ChartLine\n              data={[\n                { x: '2015', y: 1 },\n                { x: '2016', y: 2 },\n                { x: '2017', y: 5 },\n                { x: '2018', y: 3 }\n              ]}\n              name=\"cats\"\n            />\n            <ChartLine\n              data={[\n                { x: '2015', y: 2 },\n                { x: '2016', y: 1 },\n                { x: '2017', y: 7 },\n                { x: '2018', y: 4 }\n              ]}\n              name=\"dogs\"\n              style={{\n                data: {\n                  strokeDasharray: '3,3'\n                }\n              }}\n            />\n            <ChartLine\n              data={[\n                { x: '2015', y: 3 },\n                { x: '2016', y: 4 },\n                { x: '2017', y: 9 },\n                { x: '2018', y: 5 }\n              ]}\n              name=\"birds\"\n            />\n            <ChartLine\n              data={[\n                { x: '2015', y: 3 },\n                { x: '2016', y: 3 },\n                { x: '2017', y: 8 },\n                { x: '2018', y: 7 }\n              ]}\n              name=\"mice\"\n            />\n          </ChartGroup>\n        </Chart>\n      </div>\n    );\n  }\n}","title":"Green with bottom aligned legend","lang":"js"}}>

      <p {...{"className":"ws-p"}}>
        {`This demonstrates how to combine cursor and voronoi containers to display tooltips along with a cursor.`}
      </p>
    </Example>,
  'Multi-color (unordered) with responsive container': props => 
    <Example {...pageData} {...props} {...{"code":"import React from 'react';\nimport { Chart, ChartAxis, ChartGroup, ChartLine, ChartThemeColor, getResizeObserver } from '@patternfly/react-charts';\nimport { VictoryZoomContainer } from 'victory-zoom-container';\n\nclass MultiColorChart extends React.Component {\n  constructor(props) {\n    super(props);\n    this.containerRef = React.createRef();\n    this.observer = () => {};\n    this.state = {\n      width: 0\n    };\n    this.handleResize = () => {\n      if (this.containerRef.current && this.containerRef.current.clientWidth) {\n        this.setState({ width: this.containerRef.current.clientWidth });\n      }\n    };\n  }\n\n  componentDidMount() {\n    this.observer = getResizeObserver(this.containerRef.current, this.handleResize);\n    this.handleResize();\n  }\n\n  componentWillUnmount() {\n    this.observer();\n  }\n\n  render() {\n    const { width } = this.state;\n    \n    return (\n      <div ref={this.containerRef}>\n        <div style={{ height: '275px' }}>\n          <Chart\n            ariaDesc=\"Average number of pets\"\n            ariaTitle=\"Line chart example\"\n            containerComponent={<VictoryZoomContainer zoomDimension=\"x\" />}\n            legendData={[{ name: 'Cats' }, { name: 'Dogs', symbol: { type: 'dash' } }, { name: 'Birds' }, { name: 'Mice' }]}\n            legendPosition=\"bottom-left\"\n            height={275}\n            maxDomain={{y: 10}}\n            minDomain={{y: 0}}\n            padding={{\n              bottom: 75, // Adjusted to accommodate legend\n              left: 50,\n              right: 50,\n              top: 50\n            }}\n            themeColor={ChartThemeColor.multiUnordered}\n            width={width}\n           >\n            <ChartAxis tickValues={[2, 3, 4]} />\n            <ChartAxis dependentAxis showGrid tickValues={[2, 5, 8]} />\n            <ChartGroup>\n              <ChartLine\n                data={[\n                  { name: 'Cats', x: '2015', y: 1 },\n                  { name: 'Cats', x: '2016', y: 2 },\n                  { name: 'Cats', x: '2017', y: 5 },\n                  { name: 'Cats', x: '2018', y: 3 }\n                ]}\n              />\n              <ChartLine\n                data={[\n                  { name: 'Dogs', x: '2015', y: 2 },\n                  { name: 'Dogs', x: '2016', y: 1 },\n                  { name: 'Dogs', x: '2017', y: 7 },\n                  { name: 'Dogs', x: '2018', y: 4 }\n                ]}\n                style={{\n                  data: {\n                    strokeDasharray: '3,3'\n                  }\n                }}\n              />\n              <ChartLine\n                data={[\n                  { name: 'Birds', x: '2015', y: 3 },\n                  { name: 'Birds', x: '2016', y: 4 },\n                  { name: 'Birds', x: '2017', y: 9 },\n                  { name: 'Birds', x: '2018', y: 5 }\n                ]}\n              />\n              <ChartLine\n                data={[\n                  { name: 'Mice', x: '2015', y: 3 },\n                  { name: 'Mice', x: '2016', y: 3 },\n                  { name: 'Mice', x: '2017', y: 8 },\n                  { name: 'Mice', x: '2018', y: 7 }\n                ]}\n              />\n            </ChartGroup>\n          </Chart>\n        </div>\n      </div>\n    );\n  }\n}","title":"Multi-color (unordered) with responsive container","lang":"js"}}>

      <p {...{"className":"ws-p"}}>
        {`This demonstrates zoom for the x axis only.`}
      </p>
    </Example>
};

